$('document').ready(function()
{
    /* validation */
    $("#register-form").validate({
        rules:
        {
            user_email: {
                required: true,
                email: true
            }
        },
        messages:
        {
            user_email: "Enter a valid email"
        },
        submitHandler: submitForm
    });
    /* validation */

    /* form submit */
    function submitForm()
    {


        var data = $("#register-form").serialize();

        $.ajax({
            url  : 'reset.php',
            type : 'post',
            data : data,
            beforeSend: function()
            {
                $("#error").fadeOut();
                $("#btn-submit").html('<span class="glyphicon glyphicon-transfer"></span> &nbsp; authenticating...');
            },
            success :  function(data)
            {

                if(data=="reset")
                {
                    $("#error").fadeIn(1000, function(){

                        $("#btn-submit").html('<span class="fa fa-spin fa-spinner"></span> &nbsp; sending...');

                        setTimeout(function () {
                            $("#error").html('<div class="alert alert-success"><span class="fa fa-check"></span> &nbsp; Password reset successful!</div>');
                            $("#btn-submit").html('<span class="fa fa-recycle"></span> &nbsp; Reset');
                        },5000);
                    });
                }

                else if(data=='error'){
                    $("#error").fadeIn(1000, function(){

                        $("#error").html('<div class="alert alert-danger"><span class="glyphicon glyphicon-info-sign"></span> &nbsp; Sorry password reset failed, please try again!</div>');

                        $("#btn-submit").html('<span class="fa fa-recycle"></span> &nbsp; Reset');

                    });

                }

                else if(data=='0'){
                    $("#error").fadeIn(1000, function(){

                        $("#error").html('<div class="alert alert-danger"><span class="glyphicon glyphicon-info-sign"></span> &nbsp; Sorry no account found!</div>');

                        $("#btn-submit").html('<span class="fa fa-recycle"></span> &nbsp; Reset');

                    });

                }



                else{

                    $("#error").fadeIn(1000, function(){

                        $("#error").html('<div class="alert alert-danger"><span class="glyphicon glyphicon-info-sign"></span> &nbsp; Sorry an error occurred</div>');

                        $("#btn-submit").html('<span class="fa fa-recycle"></span> &nbsp; Reset');

                    });

                }
            },
            error: function(e){
                alert(e+"<br>check internet settings");
                $("#btn-submit").html('<span class="fa fa-recycle"></span> &nbsp; Reset');
            }
        });
        return false;
    }
    /* form submit */


    });


